mapper.delete:

DELETE FROM TBL_NOTI_MST
WHERE NOTI_SEQ   = #{notiSeq}
AND   FILE_GB    = #{fileGb}

mapper.update:


       UPDATE TBL_NOTI_MST 
        SET NOTI_REDCNT = NOTI_REDCNT + 1 
	  WHERE NOTI_SEQ = #{notiSeq} 
	    AND FILE_GB  = #{fileGb}


UPDATE TBL_ASQ_MST
SET 
    ACTION_YN    = 'N',
    REG_DTTM     = NOW(),
    REG_USER     = #{regUserasq},
    REG_IP       = #{regIp} 
WHERE ASQ_SEQ  = #{asqSeq} 
  AND FILE_GB  = #{fileGbasq}
  AND ACTION_YN = 'Y'
