mapper.select:
SELECT MAX(C.HOSP_CD) AS HOSP_CD, MAX(C.HOSP_NM) AS HOSP_NM, MAX(C.USER_ID) AS USER_ID, MAX(C.USER_NM) AS USER_NM, MAX(C.WINNER_YN) AS WINNER_YN, MAX(C.LAST_CONDTTM) AS LAST_CONDTTM, MAX(C.LAST_CONUSER) AS LAST_CONUSER, MAX(C.WINNER_YN) AS WINNER_YN, MAX(C.PASS_WD) AS PASS_WD, MAX(C.START_DT) AS START_DT, MAX(C.END_DT) AS END_DT, MAX(C.MAIN_GU) AS MAIN_GU, MAX(C.HOSP_UUID) AS HOSP_UUID, MAX(C.USE_NOT) AS USE_NOT, MAX(C.CONACT_GB) AS CONACT_GB , MAX(C.INS_AUTH) AS INS_AUTH , MAX(C.UPD_AUTH) AS UPD_AUTH , MAX(C.DEL_AUTH) AS DEL_AUTH , MAX(C.INQ_AUTH) AS INQ_AUTH FROM ( SELECT A.HOSP_CD, B.HOSP_NM, A.USER_ID, A.USER_NM, B.WINNER_YN, B.LAST_CONDTTM, B.LAST_CONUSER, A.PASS_WD, A.START_DT, A.END_DT, A.MAIN_GU, A.HOSP_UUID, C.INS_AUTH , C.UPD_AUTH , C.DEL_AUTH , C.INQ_AUTH , (CASE WHEN NOW() BETWEEN A.START_DT AND A.END_DT THEN 'Y' ELSE 'N' END) AS USE_NOT, ( SELECT CASE WHEN COUNT(DISTINCT CONACT_GB) = 2 AND SUM(CONACT_GB IN (1,2)) = 2 THEN 'A' WHEN COUNT(DISTINCT CONACT_GB) = 1 AND MAX(CONACT_GB) = 1 THEN '1' WHEN COUNT(DISTINCT CONACT_GB) = 1 AND MAX(CONACT_GB) = 2 THEN '2' ELSE 'N' END FROM TBL_HOSPCONT_MST WHERE HOSP_CD = #{hospCd} AND ACTION_YN = 'Y' AND NOW() BETWEEN STR_TO_DATE(ACCEPT_DT, '%Y%m%d') AND STR_TO_DATE(CLOSE_DT, '%Y%m%d') ) AS CONACT_GB FROM TBL_USER_MST A JOIN TBL_HOSP_MST B ON A.HOSP_CD = B.HOSP_CD AND A.ACTION_YN = B.ACTION_YN LEFT JOIN TBL_USERAUTH_MST C ON C.HOSP_CD = A.HOSP_CD AND C.USER_ID = A.USER_ID AND C.ACTION_YN = A.ACTION_YN WHERE A.ACTION_YN = 'Y' AND A.HOSP_CD = #{hospCd} AND A.USER_ID = #{userId}
UNION ALL
SELECT '0' AS HOSP_CD, '0' AS HOSP_NM, '0' AS USER_ID, '0' AS USER_NM, '0' AS WINNER_YN, '0' AS LAST_CONDTTM, '0' AS LAST_CONUSER, '0' AS PASS_WD, '0' AS START_DT, '0' AS END_DT, '0' AS MAIN_GU, '0' AS HOSP_UUID, '0' AS USE_NOT, '0' AS CONACT_GB , '0' AS INS_AUTH , '0' AS UPD_AUTH , '0' AS DEL_AUTH , '0' AS INQ_AUTH ) C

mapper.update:

	  UPDATE TBL_USER_MST
	     SET PASS_WD  = #{encPassWd}
       Where 1=1
           AND ACTION_YN = 'Y' 
		   AND HOSP_CD   = #{hospCd}
		   AND USER_ID   = #{userId}

mapper.delete:

DELETE FROM TBL_USER_MST
WHERE HOSP_CD = #{hospCd}
  AND USER_ID = #{userId}
  And START_DT = REPLACE(#{startDt} , '-', '')

mapper.select:

SELECT 'Y'
  FROM TBL_MEMBER_MST
 WHERE HOSP_CD = #{hospCd}
   AND EMAIL   = #{email}


	   SELECT 'Y'
	   FROM TBL_USER_MST 
        WHERE HOSP_CD   = #{hospCd} 
          AND USER_ID   = #{email} 
          AND ACTION_YN = 'Y'

SELECT COALESCE(MAX(CASE WHEN (MG_YEAR * 100 + MGMONTH) = DATE_FORMAT(DATE_SUB(CURRENT_DATE, INTERVAL 2 MONTH), '%Y%m') + 0 THEN '등록' END), '-') AS MONTH_3, COALESCE(MAX(CASE WHEN (MG_YEAR * 100 + MGMONTH) = DATE_FORMAT(DATE_SUB(CURRENT_DATE, INTERVAL 1 MONTH), '%Y%m') + 0 THEN '등록' END), '-') AS MONTH_2, COALESCE(MAX(CASE WHEN (MG_YEAR * 100 + MGMONTH) = DATE_FORMAT(DATE_SUB(CURRENT_DATE, INTERVAL 0 MONTH), '%Y%m') + 0 THEN '등록' END), '-') AS MONTH_1 FROM TBL_MAGAM_INFO WHERE HOSP_CD = #{hospCd}
UNION ALL
SELECT COALESCE(MAX(CASE WHEN JOBYYMM = DATE_FORMAT(DATE_SUB(CURRENT_DATE, INTERVAL 2 MONTH), '%Y%m') + 0 THEN '등록' END), '-') AS MONTH_3, COALESCE(MAX(CASE WHEN JOBYYMM = DATE_FORMAT(DATE_SUB(CURRENT_DATE, INTERVAL 1 MONTH), '%Y%m') + 0 THEN '등록' END), '-') AS MONTH_2, COALESCE(MAX(CASE WHEN JOBYYMM = DATE_FORMAT(DATE_SUB(CURRENT_DATE, INTERVAL 0 MONTH), '%Y%m') + 0 THEN '등록' END), '-') AS MONTH_1 FROM TBL_PAT_INDI WHERE HOSP_CD = #{hospCd}

mapper.update:

	    UPDATE TBL_HOSP_MST 
	       SET LAST_CONDTTM = NOW(), 
        	   LAST_CONUSER = #{lastConuser} 
          WHERE ACTION_YN = 'Y' 
            AND HOSP_CD = #{hospCd}
