SELECT A.SAMVER ,A.TBLINFO ,A.VERSION ,A.SEQ ,A.ITEM_NM ,A.SORT_SEQ ,A.START_POS ,A.END_POS ,A.DATA_TYPE ,A.DECIMAL_POS ,A.DATAPROC_YN ,A.DB_COLNM ,A.LAST_YN ,A.DB_COMCOLNM ,B.SUB_CODE_NM ,A.COL_SIZE ,A.REG_DTTM ,A.REG_USER ,A.REG_IP ,A.UPD_DTTM ,A.UPD_USER ,A.UPD_IP FROM TBL_SAMFVER_MST A INNER JOIN TBL_CODE_DTL B ON B.CODE_CD = 'TBLINFO' AND B.SUB_CODE = A.TBLINFO WHERE 1 = 1 AND A.ACTION_YN = 'Y'
AND (A.SAMVER LIKE CONCAT('%', #{samver}, '%') or A.VERSION = #{version} or (A.SAMVER BETWEEN #{samver} AND #{prop1}))
AND A.TBLINFO = #{tblinfo}
AND A.DATAPROC_YN = #{dataprocYn}
ORDER BY A.VERSION, A.SAMVER, B.SORT, A.TBLINFO,A.SEQ

mapper.insert:

        INSERT INTO TBL_SAMFVER_MST (
             SAMVER 
            ,TBLINFO 
            ,VERSION 
            ,SEQ 
            ,ITEM_NM 
            ,SORT_SEQ 
            ,START_POS 
            ,END_POS
            ,DATA_TYPE 
            ,DECIMAL_POS 
            ,DATAPROC_YN 
            ,DB_COLNM 
            ,LAST_YN 
            ,DB_COMCOLNM
            ,COL_SIZE 
            ,REG_DTTM 
            ,REG_USER 
            ,REG_IP 
            ,UPD_DTTM
            ,UPD_USER
            ,UPD_IP 
            ,JOB_SEQ 
            ,ACTION_YN 
        ) VALUES (
            #{samver} 
           ,#{tblinfo} 
           ,#{version} 
           ,#{seq} 
           ,#{itemNm} 
           ,#{sortSeq} 
           ,#{startPos}  
           ,#{endPos} 
           ,#{dataType}  
           ,#{decimalPos}  
           ,#{dataprocYn} 
           ,#{dbColnm}  
           ,#{lastYn}  
           ,#{dbComcolnm} 
           ,#{colSize}  
           ,now()
           ,#{regUser}  
           ,#{regIp}  
           ,now()
           ,#{updUser} 
           ,#{updIp}  
	       , (SELECT CASE WHEN MAX(A.JOB_SEQ) IS NOT NULL THEN MAX(A.JOB_SEQ) + 1 ELSE 1 END
	              FROM TBL_SAMFVER_MST A 
                 WHERE SAMVER  = #{samver}
                   AND TBLINFO = #{tblinfo} 
                   AND VERSION = #{version} 
                   AND SEQ     = #{seq}
             )     
	       ,'Y' 
        )

