mapper.insert:

        INSERT INTO TBL_CLAIMRATE_MST 
               (HOS_GRD,          CFORM_NO, CFORM_TYPE ,
                CFORM_SUB,        CFORM_IO, 
                ITEM_NO,          CODE_NO, 
                EDI_FCODE,        EDI_TCODE, 
                START_YM,         CLAIM_RATE,       
                MINOR_RATE,       REG_DTTM, 
                REG_USER,         REG_IP, 
                UPD_DTTM,         UPD_USER, 
                UPD_IP , 
                JOB_SEQ ,
                ACTION_YN 
                )
        VALUES ( #{hosGrd}   ,   #{cformNo}  , #{cformType}  ,
                 #{cformSub} ,   #{cformIo}  , 
                 #{itemNo}   ,   #{codeNo}   , 
                 #{ediFcode} ,   #{ediTcode} , 
                 #{startYm}  ,   #{claimRate}, 
                 #{minorRate},   
                 NOW()       ,   #{regUser}  ,   
                 #{regIp}    ,   NOW()       ,   
                 #{updUser}  ,   #{updIp}    , 
                 (SELECT CASE WHEN MAX(A.JOB_SEQ) IS NOT NULL THEN MAX(A.JOB_SEQ) + 1 ELSE 1 END
                   FROM TBL_CLAIMRATE_MST A 
                   WHERE A.HOP_GRD      = #{hosGrd} 
                     AND A.CFORM_NO     = #{cformNo}
                     AND A.CFORM_TYPE   = #{cformType}
                     AND A.CFORM_SUB    = #{cformSub}
                     AND A.CFORM_IO     = #{cformIo}
                     AND A.ITEM_NO      = #{itemNo}
                     AND A.CODE_NO      = #{codeNo}
                     AND A.EDI_FCODE    = #{ediFcode}
                     AND A.EDI_TCODE    = #{ediTcode}
                     AND A.START_YM     = #{startYm}
                     ), 'Y'
	          )

mapper.update:

