  WHERE HOS_GRD     = #{hosGrd} 
    AND CFORM_NO    = #{cformNo}
    AND CFORM_TYPE  = #{cformType}
    AND CFORM_SUB   = #{cformSub}
    AND CFORM_IO    = #{cformIo}
    AND ITEM_NO     = #{itemNo}
    AND CODE_NO     = #{codeNo}
    AND EDI_FCODE   = #{ediFcode}
    AND EDI_TCODE   = #{ediTcode}
    AND START_YM    = #{startYm}
    AND ACTION_YN   = 'Y'

