mapper.select:
SELECT A.DATE_YM , A.HOSP_CD , A.CLAIM_NO , A.CLFORM_VER , A.CFORM_NO , FN_GET_DTL_CODE('Z', 'INSUR_TYPE', A.INSUR_TYPE) AS INSUR_TYPE , FN_GET_DTL_CODE('Z', 'CFORM_NO', A.CFORM_NO) AS CFORM_NAME , A.TREAT_TYPE , CASE WHEN SUBSTRING(A.CFORM_NO,1,1) <> 'M' THEN FN_GET_DTL_CODE('Z', 'TREAT_TYPE', A.TREAT_TYPE) ELSE FN_GET_DTL_CODE('Z', 'TREAT_SAN_TYPE', A.TREAT_TYPE) END TREAT_NAME , A.CASE_CNT , FORMAT(IFNULL(A.TOTAL_2,0) + IFNULL(A.NON100_COST,0), 0) AS TOT_AMT , FORMAT(IFNULL(A.CLAIM_AMT,0) + IFNULL(A.NON100_CLAIM,0) + IFNULL(A.DISABLED_AMT,0), 0) AS CLAIM_AMT , FORMAT(IFNULL(A.SELF_AMT,0) + IFNULL(A.NON100_CO_PAY,0), 0) AS SELF_AMT FROM TBL_CHUNG_MST A WHERE A.HOSP_CD = #{hospCd} AND A.DATE_YM = #{dateYm}
AND A.CFORM_NO = #{cformNo}
ORDER BY A.CLAIM_NO
