mapper.insert:
        INSERT INTO TBL_FAQ_MST (
		     FILE_GB
		    ,QSTN_CONTS
		    ,ANSR_CONTS 
		    ,START_DT
		    ,END_DT
		    ,USE_YN
		    ,SORT_SEQ
		    ,REG_DTTM
		    ,REG_USER
		    ,REG_IP
		    ,UPD_DTTM
		    ,UPD_USER
		    ,UPD_IP
            ,JOB_SEQ
            ,ACTION_YN
        ) VALUES (
			 #{fileGb}
		    ,#{qstnConts}
		    ,#{ansrConts} 
		    ,#{startDt}
		    ,#{endDt}
		    ,#{useYn}
		    ,#{sortSeq}
		    ,NOW()
		    ,#{regUser}
		    ,#{regIp}
		    ,NOW()
		    ,#{updUser}
		    ,#{updIp}
			,(SELECT CASE WHEN MAX(A.JOB_SEQ) IS NOT NULL THEN MAX(A.JOB_SEQ) + 1 ELSE 1 END
			 FROM TBL_FAQ_MST A 
			 WHERE A.FAQ_SEQ    = #{faqSeq} 
			) , 'Y'
        )

mapper.update:
UPDATE TBL_FAQ_MST
SET  ACTION_YN   = 'N'
    ,UPD_DTTM    = NOW()
    ,UPD_USER    = #{updUser}
    ,UPD_IP      = #{updIp}
WHERE  FAQ_SEQ    = #{faqSeq} 
   AND ACTION_YN = 'Y'

mapper.select:
SELECT A.NOTI_SEQ , A.FILE_GB , C.SUB_CODE_NM , B.USER_NM , A.START_DT , A.END_DT, A.USE_YN , A.HOSP_CD , A.NOTI_TITLE , A.NOTI_CONTENT , A.UPDATE_SW , A.NOTI_REDCNT , A.REG_DTTM , A.REG_USER , A.REG_IP , A.UPD_DTTM , A.UPD_USER , A.UPD_IP , CASE WHEN A.FILE_GB <> '' THEN CASE WHEN ( SELECT COUNT(*) FROM TBL_FILE_MST WHERE FILE_GB = A.FILE_GB AND FILE_SEQ = A.NOTI_SEQ AND ACTION_YN = A.ACTION_YN ) > 0 THEN 'Y' ELSE 'N' END ELSE 'N' END AS FILE_YN FROM TBL_NOTI_MST A LEFT JOIN TBL_USER_MST B ON B.HOSP_CD = A.HOSP_CD AND B.USER_ID = A.UPD_USER AND B.ACTION_YN = A.ACTION_YN LEFT JOIN TBL_CODE_DTL C ON C.CODE_GB = 'Z' AND C.CODE_CD = 'NOTI_GB' AND C.SUB_CODE = A.FILE_GB AND C.ACTION_YN = A.ACTION_YN WHERE 1=1 AND A.ACTION_YN = 'Y'
AND A.FILE_GB = #{fileGb}
AND DATE_FORMAT(A.UPD_DTTM,'%Y%m%d') BETWEEN #{startDt} AND #{endDt} ORDER by A.UPD_DTTM DESC , A.FILE_GB

mapper.insert:
        INSERT INTO TBL_NOTI_MST (
	         FILE_GB   
	        ,START_DT 
		    ,END_DT 
	        ,USE_YN  
	        ,HOSP_CD  
	        ,NOTI_TITLE  
	        ,NOTI_CONTENT  
	        ,UPDATE_SW 
	        ,NOTI_REDCNT  
	        ,REG_DTTM 
	        ,REG_USER  
	        ,REG_IP  
	        ,UPD_DTTM 
	        ,UPD_USER  
	        ,UPD_IP 
            ,JOB_SEQ
            ,ACTION_YN
        ) VALUES (
	         #{fileGb}   
	        ,#{startDt} 
		    ,#{endDt} 
	        ,#{useYn}  
	        ,#{hospCd}  
	        ,#{notiTitle}  
	        ,#{notiContent}  
	        ,#{updateSw} 
	        ,#{notiRedcnt}  
	        ,NOW() 
	        ,#{regUser}  
	        ,#{regIp}  
	        ,NOW() 
	        ,#{updUser}  
	        ,#{updIp} 
			,(SELECT CASE WHEN MAX(A.JOB_SEQ) IS NOT NULL THEN MAX(A.JOB_SEQ) + 1 ELSE 1 END
		   	       FROM  TBL_NOTI_MST A 
			       WHERE A.NOTI_SEQ   = #{notiSeq} 
			         AND A.FILE_GB    = #{fileGb} 
			) , 'Y'
        )

mapper.update:
        UPDATE TBL_NOTI_MST
        SET  ACTION_YN   = 'N'
            ,UPD_DTTM    = NOW()
            ,UPD_USER    = #{updUser}
            ,UPD_IP      = #{updIp}
        WHERE NOTI_SEQ   = #{notiSeq} 
		  AND FILE_GB    = #{fileGb} 
          AND ACTION_YN = 'Y'

mapper.insert:
        INSERT INTO TBL_FILE_MST (
	         FILE_SEQ   
	        ,FILE_GB  
	        ,FILE_TITLE  
	        ,FILE_PATH  
	        ,HOSP_CD 
	        ,USE_YN  
	        ,REG_DTTM 
	        ,REG_USER  
	        ,REG_IP  
	        ,UPD_DTTM 
	        ,UPD_USER  
	        ,UPD_IP 
            ,JOB_SEQ
            ,ACTION_YN
        ) VALUES (
             #{fileSeq}  
	        ,#{fileGb}   
	        ,#{fileTitle} 
		    ,#{filePath} 
	        ,#{hospCd}  
	        ,#{useYn}  
	        ,NOW() 
	        ,#{regUser}  
	        ,#{regIp}  
	        ,NOW() 
	        ,#{updUser}  
	        ,#{updIp} 
			,(SELECT CASE WHEN MAX(A.JOB_SEQ) IS NOT NULL THEN MAX(A.JOB_SEQ) + 1 ELSE 1 END
		   	       FROM  TBL_FILE_MST A 
			       WHERE A.FILE_SEQ   = #{fileSeq} 
			         AND A.FILE_GB    = #{fileGb} 
			) , 'Y'
        )

mapper.update:
        UPDATE TBL_FILE_MST
        SET  ACTION_YN     = 'N'
            ,UPD_DTTM      = NOW()
            ,UPD_USER      = #{updUser}
            ,UPD_IP        = #{updIp}
        WHERE FILE_SEQ     = #{fileSeq} 
		  AND FILE_GB      = #{fileGb} 
          AND ACTION_YN    = 'Y' 



           AND   A.HOSP_CD = #{hospCd}

        UPDATE TBL_FILE_MST
         SET  FILE_PATH     = #{filePath}
        WHERE FILE_SEQ     = #{fileSeq} 
		  AND FILE_GB      = #{fileGb} 
		  AND SEQ          = #{seq} 
          AND ACTION_YN    = 'Y'

        UPDATE TBL_FILE_MST
        SET  ACTION_YN   = 'N'
            ,UPD_DTTM    = NOW()
            ,UPD_USER    = #{updUser}
            ,UPD_IP      = #{updIp}
        WHERE FILE_SEQ   = #{fileSeq} 
		  AND FILE_GB    = #{fileGb} 
		  AND FILE_PATH  = #{filePath}
          AND ACTION_YN  = 'Y' 



           AND   A.HOSP_CD = #{hospCd}

        UPDATE TBL_ASQ_MST
        SET  ANSR_WAN     = #{ansrWan}  
            ,ANSR_CONTS   = #{ansrConts}           
            ,UPD_DTTM     = NOW()
            ,UPD_USER     = #{updUser}
            ,UPD_IP       = #{updIp}
        WHERE ASQ_SEQ     = #{asqSeq} 
		  AND FILE_GB     = #{fileGb} 
          AND ACTION_YN   = 'Y'

        UPDATE TBL_ASQ_MST
        SET  ACTION_YN    = 'N'          
            ,UPD_DTTM     = NOW()
            ,UPD_USER     = #{updUser}
            ,UPD_IP       = #{updIp}
        WHERE ASQ_SEQ     = #{asqSeq} 
		  AND FILE_GB     = #{fileGb} 
          AND ACTION_YN   = 'Y'
