SELECT HOSP_CD, START_YY, QTER_FLAG, JOB_SEQ, ACTION_YN, HOSPGRADE, PAT_COUNT, DOC_COUNT, NUR_COUNT, NUR_S_CNT, PHAM_DAYS, TOTAL_DAY,
(SELECT MAX(GOAL_SCORE) FROM TBL_GRADE_MST WHERE HOSP_CD = #{hospCd} AND ACTION_YN = 'Y' AND START_YY = #{startYy} ) AS GOAL_SCORE , (SELECT MAX(GOAL_NAME) FROM TBL_GRADE_MST WHERE HOSP_CD = #{hospCd} AND ACTION_YN = 'Y' AND START_YY = #{startYy} ) AS GOAL_NAME ,
REG_DTTM, REG_USER, REG_IP, UPD_DTTM, UPD_USER, UPD_IP FROM TBL_GRADE_MST WHERE 1 = 1 AND HOSP_CD = #{hospCd} AND ACTION_YN = 'Y'
AND START_YY = #{startYy}
AND QTER_FLAG = #{qterFlag}
Order by HOSP_CD ASC , START_YY DESC , QTER_FLAG DESC

